import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const FinalPage = () => {
  const location = useLocation();
  const fileData = location.state;

  // Extract dimensions and metric from fileData
  const { reportName } = location.state; // Get reportName from previous page

  const [dimensions, setDimensions] = useState([]);
  const [metric, setMetric] = useState(null);
  const [refreshTime, setRefreshTime] = useState("");
  //   const [reportName, setReportName] = useState("");

  const [queryResults, setQueryResults] = useState(null);

  // Define metricAliases (same as before)
  const metricAliases = {
    totalUsers: "total_users",
    revenue: "total_revenue",
    eventCount: "total_events",
    views: "total_views",
  };

  //   console.log(
  //     JSON.stringify({
  //       dimensions,
  //       metric,
  //     })
  //   );

  useEffect(() => {
    setDimensions(fileData.dimensions || []);
    setMetric(fileData.metric || null);
    setRefreshTime(fileData.refreshTime || "");
    // setReportName(fileData.reportName || "");

    if (!metric) return; // Don't fetch if metric is missing

    //     const fetchData = async () => {
    //       try {
    //         const response = await fetch("http://localhost:3001/query-bigquery", {
    //           method: "POST",
    //           headers: { "Content-Type": "application/json" },
    //           body: JSON.stringify({
    //             dimensions,
    //             metric,
    //             refreshTime,
    //           }),
    //         });

    //         const data = await response.json();
    //         setQueryResults(data);
    //       } catch (error) {
    //         console.error("Error fetching data:", error);
    //       }
    //     };

    //     fetchData();
    //   }, [dimensions, metric, refreshTime]);
    const fetchData = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/get-file-by-report-name/${reportName}`
        );
        const fileData = await response.json();

        // Extract data from the fetched JSON
        setDimensions(fileData.dimensions || []);
        setMetric(fileData.metric || null);
        setQueryResults(fileData.results || null);
      } catch (error) {
        console.error("Error fetching file data:", error);
        // Handle error (e.g., display an error message)
      }
    };

    fetchData();
  }, [reportName, fileData]); // Re-fetch if reportName changes

  return (
    <div>
      <h1>Final Page</h1>

      <h2>{fileData.reportName}'s Report</h2>

      <h2>Query Results:</h2>

      {queryResults && queryResults.length > 0 ? (
        <table>
          <thead>
            <tr>
              {/* Dynamically create table headers */}
              {dimensions.map((dimension) => (
                <th key={dimension}>{dimension}</th>
              ))}
              <th>{metricAliases[metric]}</th>
            </tr>
          </thead>
          <tbody>
            {queryResults.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {/* Dynamically render table cells */}
                {dimensions.map((dimension) => (
                  <td key={`${rowIndex}-${dimension}`}>{row[dimension]}</td>
                ))}
                <td>{row[metricAliases[metric]]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>{queryResults ? "No data found." : "Loading data..."}</p>
      )}
    </div>
  );
};

export default FinalPage;
