import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Section3 = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Retrieve values from previous steps
  const section1Values = location.state?.section1Values || [];
  const section2Value = location.state?.section2Value || null;

  // State to manage the selected refresh time
  const [selectedRefreshTime, setSelectedRefreshTime] = useState("5 minutes"); // Default to 5 minutes

  // Handle radio button change
  const handleRefreshTimeChange = (event) => {
    setSelectedRefreshTime(event.target.value);
  };

  // Handle form submission
  const handleSubmit = () => {
    // Pass all collected data to the final page
    navigate("/final", {
      state: {
        section1Values,
        section2Value,
        section3Value: selectedRefreshTime,
      },
    });
  };

  return (
    <div>
      <h1>Refresh Time</h1>
      <p>You can only select one option:</p>

      <form>
        <label>
          <input
            type="radio"
            name="refreshTime"
            value="5 minutes"
            checked={selectedRefreshTime === "5 minutes"}
            onChange={handleRefreshTimeChange}
          />{" "}
          5 minutes
        </label>
        <label>
          <input
            type="radio"
            name="refreshTime"
            value="10 minutes"
            checked={selectedRefreshTime === "10 minutes"}
            onChange={handleRefreshTimeChange}
          />{" "}
          10 minutes
        </label>
        <label>
          <input
            type="radio"
            name="refreshTime"
            value="15 minutes"
            checked={selectedRefreshTime === "15 minutes"}
            onChange={handleRefreshTimeChange}
          />{" "}
          15 minutes
        </label>
      </form>

      <button onClick={handleSubmit}>Finish</button>
    </div>
  );
};

export default Section3;
