import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const UserInfoPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const fileData = location.state;

  // Retrieve values from previous steps
  //   const dimensions = location.state?.dimensions || [];
  //   const metric = location.state?.metric || null;
  //   const refreshTime = location.state?.refreshTime || "";
  const [dimensions, setDimensions] = useState([]);
  const [metric, setMetric] = useState(null);
  const [refreshTime, setRefreshTime] = useState("");

  const [reportName, setUserName] = useState(""); // State for user's name
  const [isLoading, setIsLoading] = useState(false); // Loading state

  const [queryResults, setQueryResults] = useState(null);

  async function fetchBigQueryData() {
    const res = await fetch("http://localhost:3001/query-bigquery", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        dimensions,
        metric,
        refreshTime,
      }),
    });
    const bigqueryData = await res.json();
    return bigqueryData;
  }

  async function saveToGCS(dataToSave) {
    const response = await fetch("http://localhost:3001/save-to-gcs", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(dataToSave),
    });

    await response.json();
  }

  const handleSubmit = async () => {
    setDimensions(fileData.dimensions || []);
    setMetric(fileData.metric || null);
    setRefreshTime(fileData.refreshTime || "");
    setUserName(fileData.reportName || "");

    if (!metric) return; // Don't fetch if metric is missing
    if (reportName.trim() === "") {
      // Basic validation (check for empty name)
      alert("Please enter your name.");
      return;
    }
    setIsLoading(true); // Set loading state

    try {
      // Fetch data from BigQuery
      const res = await fetch("http://localhost:3001/query-bigquery", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          dimensions,
          metric,
          refreshTime,
        }),
      });
      const bigqueryData = await res.json();

      // Add reportName and selection details to the data
      const dataToSave = {
        reportName,
        dimensions,
        metric,
        refreshTime,
        results: bigqueryData,
      };

      // **Wait for the first fetch to complete before the second one**
      // Save to Google Cloud Storage
      const response = await fetch("http://localhost:3001/save-to-gcs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dataToSave),
      });

      await response.json();
      //   console.log(data);

      navigate("/confirmation", { state: dataToSave });
    } catch (error) {
      console.error("Error:", error);
      // Handle errors appropriately (e.g., display error messages to the user)
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  //   const handleSubmit = () => {
  //     if (userName.trim() === "") {
  //       // Basic validation (check for empty name)
  //       alert("Please enter your name.");
  //       return;
  //     }

  //     // Pass all collected data to the final page
  //     // navigate("/final", {
  //     //   state: {
  //     //     section1Values,
  //     //     section2Value,
  //     //     userName,
  //     //   },
  //     // });
  //     navigate("/confirmation", {
  //       state: {
  //         section1Values: location.state.section1Values,
  //         section2Value: location.state.section2Value,
  //         section3Value: location.state.section3Value,
  //         userName, // Include userName from this page
  //       },
  //     });

  //     // navigate("/confirmation", { state: location.state }); // Pass the state along
  //   };

  return (
    <div>
      <h1>User Information</h1>
      <label htmlFor="reportName">Enter your name:</label>
      <input
        type="text"
        id="reportName"
        value={reportName}
        onChange={(e) => setUserName(e.target.value)}
      />
      <button onClick={handleSubmit} disabled={isLoading}>
        {isLoading ? "Processing..." : "Proceed to Results"}
      </button>
    </div>
  );
};

export default UserInfoPage;
