import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [fileList, setFileList] = useState([]); // State to store the file list

  useEffect(() => {
    // Fetch the file list when the component mounts
    const fetchFileList = async () => {
      try {
        const response = await fetch("http://localhost:3001/list-files");
        const data = await response.json();
        setFileList(data.files); // Assuming the backend returns an array named 'files'
      } catch (error) {
        console.error("Error fetching file list:", error);
        // Handle error (e.g., display an error message)
      }
    };

    fetchFileList();
  }, []); // Empty dependency array ensures this runs only once

  const handleSubmit = () => {
    navigate("/final", { state: location.state });
  };

  return (
    <div>
      <h1>List of Files in Cloud Storage</h1>

      {fileList.length > 0 ? ( // Conditionally render the table
        <table>
          <thead>
            <tr>
              <th>Filename</th>
            </tr>
          </thead>
          <tbody>
            {fileList.map((file, index) => (
              <tr key={index}>
                <td>{file.name}</td>{" "}
                {/* Assuming each file object has a 'name' property */}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No files found.</p>
      )}

      {/* ... (rest of your confirmation page content, e.g., selections, button) ... */}
    </div>
  );
};

export default ConfirmationPage;
