import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const UserInfoPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Retrieve values from previous steps
  const section1Values = location.state?.section1Values || [];
  const section2Value = location.state?.section2Value || null;

  const [userName, setUserName] = useState(""); // State for user's name

  const handleSubmit = () => {
    if (userName.trim() === "") {
      // Basic validation (check for empty name)
      alert("Please enter your name.");
      return;
    }

    // Pass all collected data to the final page
    // navigate("/final", {
    //   state: {
    //     section1Values,
    //     section2Value,
    //     userName,
    //   },
    // });
    navigate("/confirmation", {
      state: {
        section1Values: location.state.section1Values,
        section2Value: location.state.section2Value,
        section3Value: location.state.section3Value,
        userName, // Include userName from this page
      },
    });

    // navigate("/confirmation", { state: location.state }); // Pass the state along
  };

  return (
    <div>
      <h1>User Information</h1>
      <label htmlFor="userName">Enter your name:</label>
      <input
        type="text"
        id="userName"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
      />
      <button onClick={handleSubmit}>Proceed to Results</button>
    </div>
  );
};

export default UserInfoPage;
