import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import image1 from "./path/to/your/image1.jpg"; // Replace with your image path

const Section1 = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (selectedOption) {
      navigate("/section2", { state: { section1Value: selectedOption } });
    } else {
      alert("Please select an option.");
    }
  };

  return (
    <div>
      <h1>Section 1</h1>
      <img src={image1} alt="Image 1" />
      <select
        value={selectedOption}
        onChange={(e) => setSelectedOption(e.target.value)}
      >
        <option value="">Select an option</option>
        <option value="option1A">Option 1A</option>
        <option value="option1B">Option 1B</option>
        <option value="option1C">Option 1C</option>
      </select>
      <button onClick={handleSubmit}>Next</button>
    </div>
  );
};

export default Section1;
