import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
  const location = useLocation();
  const { section1Values, section2Value, section3Value, userName } =
    location.state;
  const navigate = useNavigate();

  const handleSubmit = () => {
    // Pass all data to the FinalPage
    navigate("/final", { state: location.state });
  };

  return (
    <div>
      <h1>Confirm Your Selections</h1>
      <h2>Dimensions:</h2>
      <ul>
        {section1Values.map((value, index) => (
          <li key={index}>{value}</li>
        ))}
      </ul>
      <p>Metric: {section2Value}</p>
      <p>Refresh Time: {section3Value}</p>
      <p>User Name: {userName}</p>
      <button onClick={handleSubmit}>Proceed to Results</button>
    </div>
  );
};

export default ConfirmationPage;
