import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const UserInfoPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Retrieve values from previous steps
  const section1Values = location.state?.section1Values || [];
  const section2Value = location.state?.section2Value || null;

  const [userName, setUserName] = useState(""); // State for user's name

  const handleSubmit = async () => {
    if (userName.trim() === "") {
      // Basic validation (check for empty name)
      alert("Please enter your name.");
      return;
    }
    try {
      // Send data to backend to be saved to GCS
      const response = await fetch("http://localhost:3001/save-to-gcs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(location.state),
      });

      const data = await response.json();
      console.log(data); // Log the response from the server (optional)

      // After saving, you can navigate to the FinalPage or elsewhere
      navigate("/confirmation", { state: location.state });
    } catch (error) {
      console.error("Error saving data:", error);
      // Handle the error (e.g., display an error message to the user)
    }
  };

