import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
// import image3 from "./path/to/your/image3.jpg";

const Section3 = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  const handleSubmit = () => {
    if (selectedOption) {
      navigate("/final", {
        state: { ...location.state, section3Value: selectedOption },
      });
    } else {
      alert("Please select an option.");
    }
  };

  return (
    <div>
      <h1>Section 3</h1>
      {/* <img src={image3} alt="Image 3" /> */}
      <select
        value={selectedOption}
        onChange={(e) => setSelectedOption(e.target.value)}
      >
        <option value="">Select an option</option>
        <option value="option3A">Option 3A</option>
        <option value="option3B">Option 3B</option>
        <option value="option3C">Option 3C</option>
      </select>
      <button onClick={handleSubmit}>Finish</button>
    </div>
  );
};

export default Section3;
